const Tab = createBottomTabNavigator();
const Drawer = createDrawerNavigator();
const Stack = createStackNavigator();

const ManageUserStack = createStackNavigator();

LogBox.ignoreLogs(['Setting a timer']);

function Home() {
  return (
    <Tab.Navigator
      screenOptions={({ route }) => ({
           tabBarIcon: ({ focused, color, size }) => {
             let iconName;
             if (route.name === 'Planning') {
               iconName = 'calendar-today';
             } else if (route.name === 'Contact') {
               iconName = 'contact-phone';
             } else if (route.name === 'Mes Missions') {
               iconName = 'assignment';
             }
             return <Icon name={iconName} type='material' size={size} color={color} />;
           },
         })}
         tabBarOptions={{
           activeTintColor: 'white',
           inactiveTintColor: 'gray',
           activeBackgroundColor: '#033664',
           inactiveBackgroundColor: '#033664',
         }}>
      <Tab.Screen name="Planning" component={Planning} />
      <Tab.Screen
        name="Contact"
        component={Personnel}/>
{/*        <Tab.Screen
          name="Mes Missions"
          component={MesMissions}/>*/}
    </Tab.Navigator>
  );
}

const HeaderRightPlanning = () => {
  const navigation = useNavigation();
  return ( <Text></Text> );
}

const HeaderRightContacts = () => {
  const navigation = useNavigation();
  return (
    <TouchableOpacity style={{ justifyContent:'center', paddingRight:10 }} onPress={() => navigation.navigate('AjouterContact')}>
      <Icon
        name="add"
        type="material"
        color="#fff"
      />
      <Text style={{color:'white'}}>Ajouter</Text>
    </TouchableOpacity>
  );
}

const HeaderRightMissions = () => {
  return (
    <Text> </Text>
  );
}

function ManageUsersNav(){
  return(
    <ManageUserStack.Navigator>
      <ManageUserStack.Screen
        name="Gestion des utilisateurs"
        component={ManageUsers}
        options={({ navigation, route }) => ({
          title: 'Gestion des utilisateurs',
          headerStyle: {
            backgroundColor: '#033664',
          },
          headerTitleAlign:'center',
          headerTintColor: '#fff',
           headerLeft: () => (
             <TouchableOpacity onPress={() => navigation.goBack()}>
               <Icon
                 style={{ paddingLeft: 10 }}
                 name="arrow-back"
                 type="material"
                 color="#fff" />
               </TouchableOpacity>
          )
        })}/>
        <ManageUserStack.Screen
          name="AjouterProfil"
          component={AjouterProfil}
          options={({ navigation, route }) => ({
            title: 'Ajouter un Profil',
            headerStyle: {
              backgroundColor: '#033664',
            },
            headerTitleAlign:'center',
            headerTintColor: '#fff',
            headerLeft: () => (
              <TouchableOpacity onPress={() => navigation.goBack()}>
                <Icon
                  style={{ paddingLeft: 10 }}
                  name="arrow-back"
                  type="material"
                   color="#fff" />
                 </TouchableOpacity>
            )
          })}/>
      <ManageUserStack.Screen
        name="DetailsUser"
        component={DetailsUser}
        options={({ navigation, route }) => ({
          title: 'Détails de l\'utilisateur',
          headerStyle: {
            backgroundColor: '#033664',
          },
          headerTitleAlign:'center',
          headerTintColor: '#fff',
           headerLeft: () => (
             <TouchableOpacity onPress={() => navigation.goBack()}>
               <Icon
                 style={{ paddingLeft: 10 }}
                 name="arrow-back"
                 type="material"
                 color="#fff" />
               </TouchableOpacity>
          )
        })}/>
    </ManageUserStack.Navigator>
  )
}

function StackApp() {

  function getHeaderTitle(route) {
  const routeName = getFocusedRouteNameFromRoute(route) ?? 'Planning';
    switch (routeName) {
      case 'Planning':
        return 'Planning';
      case 'Contact':
        return 'Contact';
      case 'Mes Missions':
        return 'Mes Missions';
    }
  }

  function getHeaderRight(route) {
  const routeName = getFocusedRouteNameFromRoute(route) ?? 'Planning';
    switch (routeName) {
      case 'Planning':
        return <HeaderRightPlanning/>;
      case 'Personnel':
        return <HeaderRightContacts/>;
      case 'DetailsMission':
        return <HeaderRightMissions/>;
      case 'Mes Missions':
        return <HeaderRightMissions/>;
    }
  }

  return (
      <Stack.Navigator>
        <Stack.Screen
          name="Login"
          component={Login}
          options={({ navigation, route }) => ({
            title: 'Connexion',
            headerStyle: {
              backgroundColor: '#033664',
            },
            headerTitleAlign:'center',
            headerTintColor: '#fff',
            headerShown: false
          })}/>
        <Stack.Screen
          name="Planning"
          component={Home}
          options={({ navigation, route }) => ({
            title: 'Planning',
            headerStyle: {
              backgroundColor: '#033664',
            },
            headerTitleAlign:'center',
            headerTintColor: '#fff',
            headerRight: () => getHeaderRight(route),
           headerLeft: () => (
             <TouchableOpacity onPress={() => navigation.openDrawer()}>
               <Icon
                 style={{ paddingLeft: 10 }}
                 name="menu"
                 type="material"
                 color="#fff" />
               </TouchableOpacity>
          ),
           headerTitle: getHeaderTitle(route, navigation),
          })}
        />
        <Stack.Screen
          name="DetailsMission"
          component={DetailsMission}
          options={({ navigation, route }) => ({
            title: 'Détails de la mission',
            headerStyle: {
              backgroundColor: '#033664',
            },
            headerTitleAlign:'center',
            headerTintColor: '#fff',
          })}/>
        <Stack.Screen
          name="GalleryPhotoMission"
          component={GalleryPhotoMission}
          options={{
            title: 'Détails de la mission',
            headerStyle: {
              backgroundColor: '#033664',
            },
            headerTitleAlign:'center',
            headerTintColor: '#fff',
          }}/>
          <Stack.Screen
            name="Ajouter"
            component={Ajouter}
            options={{
              title: 'Créer une mission',
              headerStyle: {
                backgroundColor: '#033664',
              },
              headerTitleAlign:'center',
              headerTintColor: '#fff',
            }}/>
            <Stack.Screen
              name="AjouterContact"
              component={AjouterContact}
              options={{
                title: 'Ajouter un contact',
                headerStyle: {
                  backgroundColor: '#033664',
                },
                headerTitleAlign:'center',
                headerTintColor: '#fff',
              }}/>
              <Stack.Screen
                name="SelectionTypeObseques"
                component={SelectionTypeObseques}
                options={{
                  title: 'Obsèques',
                  headerStyle: {
                    backgroundColor: '#033664',
                  },
                  headerTitleAlign:'center',
                  headerTintColor: '#fff',
                }}/>
                <Stack.Screen
                  name="IsCeremonie"
                  component={IsCeremonie}
                  options={{
                    title: 'Obsèques',
                    headerStyle: {
                      backgroundColor: '#033664',
                    },
                    headerTitleAlign:'center',
                    headerTintColor: '#fff',
                  }}/>
                  <Stack.Screen
                    name="IsArriveeCorps"
                    component={IsArriveeCorps}
                    options={{
                      title: 'Obsèques',
                      headerStyle: {
                        backgroundColor: '#033664',
                      },
                      headerTitleAlign:'center',
                      headerTintColor: '#fff',
                    }}/>
                <Stack.Screen
                  name="ObsequesSecondScreen"
                  component={ObsequesSecondScreen}
                  options={{
                    title: 'Cérémonie',
                    headerStyle: {
                      backgroundColor: '#033664',
                    },
                    headerTitleAlign:'center',
                    headerTintColor: '#fff',
                  }}/>
                  <Stack.Screen
                    name="InhumOuCrema"
                    component={InhumOuCrema}
                    options={{
                      title: 'InhumOuCrema',
                      headerStyle: {
                        backgroundColor: '#033664',
                      },
                      headerTitleAlign:'center',
                      headerTintColor: '#fff',
                    }}/>
        <Stack.Screen
          name="FormulaireMission"
          component={FormulaireMission}
          options={{
            title: 'Cérémonie',
            headerStyle: {
              backgroundColor: '#033664',
            },
            headerTitleAlign:'center',
            headerTintColor: '#fff',
          }}/>
          <Stack.Screen
            name="InfoBiereFermeture"
            component={InfoBiereFermeture}
            options={{
              title: 'Informations',
              headerStyle: {
                backgroundColor: '#033664',
              },
              headerTitleAlign:'center',
              headerTintColor: '#fff',
            }}/>
          <Stack.Screen
            name="ObservationCeremonie"
            component={ObservationCeremonie}
            options={{
              title: 'Cérémonie',
              headerStyle: {
                backgroundColor: '#033664',
              },
              headerTitleAlign:'center',
              headerTintColor: '#fff',
            }}/>

      <Stack.Screen
        name="InfoCimetiere"
        component={InfoCimetiere}
        options={{
          title: 'Cimetière',
          headerStyle: {
            backgroundColor: '#033664',
          },
          headerTitleAlign:'center',
          headerTintColor: '#fff',
        }}/>
        <Stack.Screen
          name="Modal"
          component={ModalView}
          options={{
            title: 'Modal',
            headerStyle: {
              backgroundColor: '#033664',
            },
            headerTitleAlign:'center',
            headerTintColor: '#fff',
          }}/>
          <Stack.Screen
            name="EditCeremonie"
            component={EditCeremonie}
            options={{
              title: 'Modification',
              headerStyle: {
                backgroundColor: '#033664',
              },
              headerTitleAlign:'center',
              headerTintColor: '#fff',
            }}/>
            <Stack.Screen
              name="FormulaireMaconnerie"
              component={FormulaireMaconnerie}
              options={{
                title: 'Maçonnerie',
                headerStyle: {
                  backgroundColor: '#033664',
                },
                headerTitleAlign:'center',
                headerTintColor: '#fff',
              }}/>
              <Stack.Screen
                name="AjouterPhotoMaconnerie"
                component={AjouterPhotoMaconnerie}
                options={{
                  title: 'Maçonnerie',
                  headerStyle: {
                    backgroundColor: '#033664',
                  },
                  headerTitleAlign:'center',
                  headerTintColor: '#fff',
                }}/>
                <Stack.Screen
                  name="PhotoMaconnerie"
                  component={PhotoMaconnerie}
                  options={{
                    title: 'Maçonnerie',
                    headerStyle: {
                      backgroundColor: '#033664',
                    },
                    headerTitleAlign:'center',
                    headerTintColor: '#fff',
                  }}/>
      </Stack.Navigator>

  );
}

